@import "tailwindcss";
@import "tw-animate-css";

/* ============================================================
   GLITCHCN/UI - Dual Theme System
   Themes: Edgerunners (cyberpunk neon) / Armored Core (military HUD)
   ============================================================ */

@layer base {
  :root {
    /* Font — uncomment one */
    /* --font-mono: "Share Tech Mono", monospace; */
    /* --font-mono: "Bitcount Grid Single", monospace; */
    /* --font-mono: "Space Mono", monospace; */
    /* --font-mono: "JetBrains Mono", monospace; */
    /* --font-mono: "Rajdhani", sans-serif; */
    --font-mono: "Orbitron", sans-serif;
    --radius: 0.625rem;
  }
}

.font-mono {
  font-family: var(--font-mono);
}

html,
body {
  overflow-y: scroll;
  overflow-x: hidden;
}

html::-webkit-scrollbar,
body::-webkit-scrollbar {
  display: none;
}

html {
  -ms-overflow-style: none;
  scrollbar-width: none;
}

@layer utilities {
  .scrollbar-hide::-webkit-scrollbar {
    display: none;
  }
  .scrollbar-hide {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }
}

/* ============================================================
   THEME: EDGERUNNERS
   Hot pink/magenta + neon cyan, glitch effects, asymmetric cuts
   ============================================================ */

[data-theme="edgerunners"] {
  /* Core Colors */
  --background: oklch(0.08 0.04 300);
  --foreground: oklch(0.95 0.01 300);
  --card: oklch(0.12 0.035 300);
  --card-foreground: oklch(0.95 0.01 300);
  --popover: oklch(0.12 0.035 300);
  --popover-foreground: oklch(0.95 0.01 300);
  --primary: oklch(0.65 0.29 350);
  --primary-foreground: oklch(0.98 0 0);
  --secondary: oklch(0.80 0.15 195);
  --secondary-foreground: oklch(0.10 0 0);
  --muted: oklch(0.18 0.03 300);
  --muted-foreground: oklch(0.65 0.02 300);
  --accent: oklch(0.88 0.18 95);
  --accent-foreground: oklch(0.10 0 0);
  --destructive: oklch(0.65 0.28 25);
  --destructive-foreground: oklch(0.98 0 0);
  --border: oklch(0.65 0.29 350 / 20%);
  --input: oklch(1 0 0 / 10%);
  --ring: oklch(0.65 0.29 350);

  /* Sidebar */
  --sidebar: oklch(0.10 0.035 300);
  --sidebar-foreground: oklch(0.95 0.01 300);
  --sidebar-primary: oklch(0.65 0.29 350);
  --sidebar-primary-foreground: oklch(0.98 0 0);
  --sidebar-accent: oklch(0.18 0.03 300);
  --sidebar-accent-foreground: oklch(0.95 0.01 300);
  --sidebar-border: oklch(0.65 0.29 350 / 15%);
  --sidebar-ring: oklch(0.65 0.29 350);

  /* Chart */
  --chart-1: oklch(0.65 0.29 350);
  --chart-2: oklch(0.80 0.15 195);
  --chart-3: oklch(0.88 0.18 95);
  --chart-4: oklch(0.60 0.25 280);
  --chart-5: oklch(0.65 0.28 25);

  /* Glow (raw RGB for box-shadow rgba) */
  --glow-rgb: 255, 0, 127;
  --glow-secondary-rgb: 0, 220, 255;

  /* Effects */
  --scanline-opacity: 0.04;
  --scanline-speed: 6s;
  --corner-size: 14px;

  /* Augmented-ui: asymmetric TL+BR only (0px = disabled) */
  --aug-card-tl: 10px;
  --aug-card-tr: 0px;
  --aug-card-br: 10px;
  --aug-card-bl: 0px;
  --aug-btn-tl: 5px;
  --aug-btn-tr: 0px;
  --aug-btn-br: 5px;
  --aug-btn-bl: 0px;
  --aug-badge-tl: 3px;
  --aug-badge-tr: 0px;
  --aug-badge-br: 3px;
  --aug-badge-bl: 0px;
}

/* ============================================================
   THEME: ARMORED CORE
   Tactical orange + HUD green, grid overlays, symmetric octagonal cuts
   ============================================================ */

[data-theme="armored-core"] {
  /* Core Colors */
  --background: oklch(0.08 0.012 240);
  --foreground: oklch(0.88 0.01 200);
  --card: oklch(0.12 0.012 240);
  --card-foreground: oklch(0.88 0.01 200);
  --popover: oklch(0.12 0.012 240);
  --popover-foreground: oklch(0.88 0.01 200);
  --primary: oklch(0.75 0.17 60);
  --primary-foreground: oklch(0.08 0 0);
  --secondary: oklch(0.68 0.18 155);
  --secondary-foreground: oklch(0.08 0 0);
  --muted: oklch(0.18 0.01 240);
  --muted-foreground: oklch(0.55 0.01 200);
  --accent: oklch(0.60 0.22 30);
  --accent-foreground: oklch(0.98 0 0);
  --destructive: oklch(0.55 0.25 25);
  --destructive-foreground: oklch(0.98 0 0);
  --border: oklch(0.75 0.17 60 / 15%);
  --input: oklch(1 0 0 / 8%);
  --ring: oklch(0.75 0.17 60);

  /* Sidebar */
  --sidebar: oklch(0.10 0.012 240);
  --sidebar-foreground: oklch(0.88 0.01 200);
  --sidebar-primary: oklch(0.75 0.17 60);
  --sidebar-primary-foreground: oklch(0.08 0 0);
  --sidebar-accent: oklch(0.18 0.01 240);
  --sidebar-accent-foreground: oklch(0.88 0.01 200);
  --sidebar-border: oklch(0.75 0.17 60 / 10%);
  --sidebar-ring: oklch(0.75 0.17 60);

  /* Chart */
  --chart-1: oklch(0.75 0.17 60);
  --chart-2: oklch(0.68 0.18 155);
  --chart-3: oklch(0.60 0.22 30);
  --chart-4: oklch(0.55 0.15 200);
  --chart-5: oklch(0.70 0.12 90);

  /* Glow */
  --glow-rgb: 255, 140, 0;
  --glow-secondary-rgb: 0, 200, 100;

  /* Effects */
  --scanline-opacity: 0.02;
  --scanline-speed: 10s;
  --corner-size: 10px;

  /* Augmented-ui: symmetric 4-corner */
  --aug-card-tl: 6px;
  --aug-card-tr: 6px;
  --aug-card-br: 6px;
  --aug-card-bl: 6px;
  --aug-btn-tl: 4px;
  --aug-btn-tr: 4px;
  --aug-btn-br: 4px;
  --aug-btn-bl: 4px;
  --aug-badge-tl: 3px;
  --aug-badge-tr: 3px;
  --aug-badge-br: 3px;
  --aug-badge-bl: 3px;
}

/* ============================================================
   TAILWIND v4 @theme inline
   ============================================================ */

@theme inline {
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-sidebar-ring: var(--sidebar-ring);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar: var(--sidebar);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --font-sans: var(--font-geist-sans);
  --font-mono: var(--font-mono);
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
}

/* ============================================================
   BASE STYLES
   ============================================================ */

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
  }
}

/* ============================================================
   EFFECT UTILITY CLASSES
   Extracted from inline pseudo-element classes across components
   ============================================================ */

/* ============================================================
   AUGMENTED-UI UTILITY CLASSES
   Forward theme variables to augmented-ui's --aug-* system
   ============================================================ */

.aug-card[data-augmented-ui] {
  --aug-tl: var(--aug-card-tl);
  --aug-tr: var(--aug-card-tr);
  --aug-br: var(--aug-card-br);
  --aug-bl: var(--aug-card-bl);
  --aug-border-all: 1px;
  --aug-border-bg: var(--border);
}

.aug-button[data-augmented-ui] {
  --aug-tl: var(--aug-btn-tl);
  --aug-tr: var(--aug-btn-tr);
  --aug-br: var(--aug-btn-br);
  --aug-bl: var(--aug-btn-bl);
  --aug-border-all: 1px;
  --aug-border-bg: var(--primary);
}

.aug-badge[data-augmented-ui] {
  --aug-tl: var(--aug-badge-tl);
  --aug-tr: var(--aug-badge-tr);
  --aug-br: var(--aug-badge-br);
  --aug-bl: var(--aug-badge-bl);
  --aug-border-all: 1px;
  --aug-border-bg: var(--border);
}


@layer utilities {
  /* Scanline overlay - both themes, color from variables */
  .effect-scanline {
    position: relative;
    overflow: hidden;
  }
  .effect-scanline::before {
    content: "";
    position: absolute;
    inset: 0;
    background: linear-gradient(
      0deg,
      transparent 0%,
      rgba(var(--glow-rgb), var(--scanline-opacity)) 50%,
      transparent 100%
    );
    background-size: 100% 4px;
    animation: scanline var(--scanline-speed) linear infinite;
    pointer-events: none;
    z-index: 0;
  }

  /* Corner bracket decorator - 4 corners via background-image gradients */
  .effect-corner {
    --_corner-color: rgba(var(--glow-secondary-rgb), var(--corner-opacity, 0.6));
    --_corner-w: 2px;
  }
  .effect-corner::after {
    content: "";
    position: absolute;
    inset: 0;
    pointer-events: none;
    z-index: 2;
    transition: filter 300ms;
    background-image:
      /* ── Top-left ── */
      linear-gradient(var(--_corner-color), var(--_corner-color)),
      linear-gradient(var(--_corner-color), var(--_corner-color)),
      /* ── Top-right ── */
      linear-gradient(var(--_corner-color), var(--_corner-color)),
      linear-gradient(var(--_corner-color), var(--_corner-color)),
      /* ── Bottom-left ── */
      linear-gradient(var(--_corner-color), var(--_corner-color)),
      linear-gradient(var(--_corner-color), var(--_corner-color)),
      /* ── Bottom-right ── */
      linear-gradient(var(--_corner-color), var(--_corner-color)),
      linear-gradient(var(--_corner-color), var(--_corner-color));
    background-size:
      var(--corner-size) var(--_corner-w),   /* TL horizontal */
      var(--_corner-w) var(--corner-size),   /* TL vertical */
      var(--corner-size) var(--_corner-w),   /* TR horizontal */
      var(--_corner-w) var(--corner-size),   /* TR vertical */
      var(--corner-size) var(--_corner-w),   /* BL horizontal */
      var(--_corner-w) var(--corner-size),   /* BL vertical */
      var(--corner-size) var(--_corner-w),   /* BR horizontal */
      var(--_corner-w) var(--corner-size);   /* BR vertical */
    background-position:
      0 0,       0 0,       /* TL */
      100% 0,    100% 0,    /* TR */
      0 100%,    0 100%,    /* BL */
      100% 100%, 100% 100%; /* BR */
    background-repeat: no-repeat;
  }
  .effect-corner:hover::after {
    filter: drop-shadow(0 0 4px var(--_corner-color));
    --corner-opacity: 0.9;
  }

  /* Inner element equivalents (for augmented-ui compat — no pseudo conflicts) */
  .effect-scanline-inner {
    position: absolute;
    inset: 0;
    background: linear-gradient(
      0deg,
      transparent 0%,
      rgba(var(--glow-rgb), var(--scanline-opacity)) 50%,
      transparent 100%
    );
    background-size: 100% 4px;
    animation: scanline var(--scanline-speed) linear infinite;
    pointer-events: none;
    z-index: 0;
  }

  .effect-corner-inner {
    --_corner-color: rgba(var(--glow-secondary-rgb), var(--corner-opacity, 0.6));
    --_corner-w: 2px;
    position: absolute;
    inset: 0;
    pointer-events: none;
    z-index: 2;
    transition: filter 300ms;
    background-image:
      linear-gradient(var(--_corner-color), var(--_corner-color)),
      linear-gradient(var(--_corner-color), var(--_corner-color)),
      linear-gradient(var(--_corner-color), var(--_corner-color)),
      linear-gradient(var(--_corner-color), var(--_corner-color)),
      linear-gradient(var(--_corner-color), var(--_corner-color)),
      linear-gradient(var(--_corner-color), var(--_corner-color)),
      linear-gradient(var(--_corner-color), var(--_corner-color)),
      linear-gradient(var(--_corner-color), var(--_corner-color));
    background-size:
      var(--corner-size) var(--_corner-w),
      var(--_corner-w) var(--corner-size),
      var(--corner-size) var(--_corner-w),
      var(--_corner-w) var(--corner-size),
      var(--corner-size) var(--_corner-w),
      var(--_corner-w) var(--corner-size),
      var(--corner-size) var(--_corner-w),
      var(--_corner-w) var(--corner-size);
    background-position:
      0 0,       0 0,
      100% 0,    100% 0,
      0 100%,    0 100%,
      100% 100%, 100% 100%;
    background-repeat: no-repeat;
  }

  /* Separator gradient line */
  .effect-separator-bottom {
    position: relative;
  }
  .effect-separator-bottom::before {
    content: "";
    position: absolute;
    inset-inline: 0;
    bottom: 0;
    height: 1px;
    background: linear-gradient(
      to right,
      transparent,
      rgba(var(--glow-rgb), 0.5),
      transparent
    );
  }

  .effect-separator-top {
    position: relative;
  }
  .effect-separator-top::before {
    content: "";
    position: absolute;
    inset-inline: 0;
    top: 0;
    height: 1px;
    background: linear-gradient(
      to right,
      transparent,
      rgba(var(--glow-rgb), 0.5),
      transparent
    );
  }
}

/* ============================================================
   THEME-SPECIFIC EFFECTS
   ============================================================ */

/* Edgerunners: Asymmetric 2-corner brackets (TL + BR only) */
[data-theme="edgerunners"] .effect-corner::after {
  --corner-opacity: 0.7;
  background-image:
    /* ── Top-left ── */
    linear-gradient(var(--_corner-color), var(--_corner-color)),
    linear-gradient(var(--_corner-color), var(--_corner-color)),
    /* ── Top-right: hidden ── */
    none, none,
    /* ── Bottom-left: hidden ── */
    none, none,
    /* ── Bottom-right ── */
    linear-gradient(var(--_corner-color), var(--_corner-color)),
    linear-gradient(var(--_corner-color), var(--_corner-color));
  filter: drop-shadow(0 0 3px rgba(var(--glow-secondary-rgb), 0.4));
}
[data-theme="edgerunners"] .effect-corner:hover::after {
  filter: drop-shadow(0 0 6px rgba(var(--glow-secondary-rgb), 0.7));
}

/* Armored Core: Symmetric 4-corner targeting reticle (dashed) */
[data-theme="armored-core"] .effect-corner {
  --_corner-w: 1.5px;
  --corner-opacity: 0.5;
}
[data-theme="armored-core"] .effect-corner::after {
  background-image:
    /* ── Top-left ── */
    repeating-linear-gradient(to right, var(--_corner-color) 0 3px, transparent 3px 6px),
    repeating-linear-gradient(to bottom, var(--_corner-color) 0 3px, transparent 3px 6px),
    /* ── Top-right ── */
    repeating-linear-gradient(to left, var(--_corner-color) 0 3px, transparent 3px 6px),
    repeating-linear-gradient(to bottom, var(--_corner-color) 0 3px, transparent 3px 6px),
    /* ── Bottom-left ── */
    repeating-linear-gradient(to right, var(--_corner-color) 0 3px, transparent 3px 6px),
    repeating-linear-gradient(to top, var(--_corner-color) 0 3px, transparent 3px 6px),
    /* ── Bottom-right ── */
    repeating-linear-gradient(to left, var(--_corner-color) 0 3px, transparent 3px 6px),
    repeating-linear-gradient(to top, var(--_corner-color) 0 3px, transparent 3px 6px);
}
[data-theme="armored-core"] .effect-corner:hover::after {
  --corner-opacity: 0.8;
  filter: drop-shadow(0 0 3px rgba(var(--glow-secondary-rgb), 0.3));
}

/* Inner element equivalents for theme-specific corner brackets */
[data-theme="edgerunners"] .effect-corner-inner {
  --corner-opacity: 0.7;
  background-image:
    linear-gradient(var(--_corner-color), var(--_corner-color)),
    linear-gradient(var(--_corner-color), var(--_corner-color)),
    none, none,
    none, none,
    linear-gradient(var(--_corner-color), var(--_corner-color)),
    linear-gradient(var(--_corner-color), var(--_corner-color));
  filter: drop-shadow(0 0 3px rgba(var(--glow-secondary-rgb), 0.4));
}

[data-theme="armored-core"] .effect-corner-inner {
  --_corner-w: 1.5px;
  --corner-opacity: 0.5;
  background-image:
    repeating-linear-gradient(to right, var(--_corner-color) 0 3px, transparent 3px 6px),
    repeating-linear-gradient(to bottom, var(--_corner-color) 0 3px, transparent 3px 6px),
    repeating-linear-gradient(to left, var(--_corner-color) 0 3px, transparent 3px 6px),
    repeating-linear-gradient(to bottom, var(--_corner-color) 0 3px, transparent 3px 6px),
    repeating-linear-gradient(to right, var(--_corner-color) 0 3px, transparent 3px 6px),
    repeating-linear-gradient(to top, var(--_corner-color) 0 3px, transparent 3px 6px),
    repeating-linear-gradient(to left, var(--_corner-color) 0 3px, transparent 3px 6px),
    repeating-linear-gradient(to top, var(--_corner-color) 0 3px, transparent 3px 6px);
}

/* Armored Core: Grid overlay */
[data-theme="armored-core"] .effect-grid::before {
  content: "";
  position: absolute;
  inset: 0;
  background-image:
    linear-gradient(rgba(var(--glow-secondary-rgb), 0.03) 1px, transparent 1px),
    linear-gradient(90deg, rgba(var(--glow-secondary-rgb), 0.03) 1px, transparent 1px);
  background-size: 20px 20px;
  pointer-events: none;
  z-index: 0;
}

/* ============================================================
   KEYFRAMES
   ============================================================ */

@keyframes scanline {
  0% {
    transform: translateY(-100%);
  }
  100% {
    transform: translateY(100%);
  }
}

@keyframes glitch {
  0%, 100% {
    text-shadow:
      -2px 0 rgba(var(--glow-rgb), 0.7),
      2px 0 rgba(var(--glow-secondary-rgb), 0.7);
  }
  25% {
    text-shadow:
      2px 0 rgba(var(--glow-rgb), 0.7),
      -2px 0 rgba(var(--glow-secondary-rgb), 0.7);
  }
  50% {
    text-shadow:
      -1px 1px rgba(var(--glow-rgb), 0.5),
      1px -1px rgba(var(--glow-secondary-rgb), 0.5);
  }
  75% {
    text-shadow:
      1px 0 rgba(var(--glow-rgb), 0.7),
      -1px 0 rgba(var(--glow-secondary-rgb), 0.7);
  }
}

@keyframes hud-flicker {
  0%, 100% {
    opacity: 1;
  }
  92% {
    opacity: 1;
  }
  93% {
    opacity: 0.8;
  }
  94% {
    opacity: 1;
  }
  96% {
    opacity: 0.9;
  }
  97% {
    opacity: 1;
  }
}
